var Login = function () {
    var r = function () {
        $("#forget-password").click(function () {
            $(".login-form").hide(), $(".forget-form").show()
        }),
        $("#back-btn").click(function () {
            $(".login-form").show(), $(".forget-form").hide()
        })
    };
    return {
        init: function () {
            r(),
            $(".login-bg").backstretch(["assets/layouts/layout5/img/bg1.jpg", "assets/layouts/layout5/img/bg2.jpg", "assets/layouts/layout5/img/bg3.jpg"], { fade: 1e3, duration: 8e3 }),
            $(".forget-form").hide()
        }
    }
}();
jQuery(document).ready(function () { Login.init() });